import React, { useEffect, useState } from 'react'
import {API_KEY,imageUrl} from  '../../constants/constants'
import axios from '../../axios'
import './RowPost.css'
import Youtube from 'react-youtube'
function RowPost(props) {
  const[movies,setMovies]=useState([])
  const[urlId,setUrlId]=useState('')
  useEffect(()=>{
    axios.get(props.url).then(response=>{
      // console.log(response.data)
      setMovies(response.data.results)
    })
  })
  const opts = {
    height: '390',
    width: '100%',
    playerVars: {
      // https://developers.google.com/youtube/player_parameters
      autoplay: 0,
    },
  };
  const handleMovie =(id)=>{
    console.log(id)
    axios.get(`movie/${id}/videos?language=en-US&api_key=${API_KEY}`).then(response=>{
      if(response.data.results.length!== 0){
        setUrlId(response)
      }
    })
  }
  return (
     <div className='row'>
        <h2>{props.title}</h2>
        <div className='posters'>
          {movies.map((obj)=>
            <img onClick={()=>handleMovie(obj.id)} className={props.isSmall ? 'smallposter':'poster'}alt='poster' src={`${imageUrl+obj.backdrop_path}`}></img>
          )}
        </div>
        <div>
        <Youtube opts={opts} videoId="2g811Eo7K8U"/>
        </div>
    </div >
  )
}

export default RowPost
